package com.bit.first;

public class Value {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println(30+40);
		
		// 숫자 30을 저장하고
		// intNum1 이라고 이름(Lable)을 붙인다.
		// 이때 intNum1 을 변수라고 한다.
		int intNum1 = 30;
		int intNum2 = 40;
		System.out.println(intNum1 + intNum2);
		

		// 숫자 50을 저장하고
		// intNum3 라고 이름을 붙인다.
		int intNum3 = 50;
		int intNum4 = 60;
		
		// intNum3 에 저장된 값 50과
		// intNum4 에 저장된 값 60을 더하여 저장하고
		// intSum이라고 이름 붙인다.
		int intSum = intNum3 + intNum4 ;
		System.out.println(intSum);
		
		int a = 3;
		a=2;
		a=1;
		a=0;
	}

}
