package com.bit.logics;

public class Logics2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int intNum = 33;
		if(intNum % 2 == 0) { // 2로 나눈 나머지가 0이면
			System.out.println(intNum + "짝수입니다");
		} else {
			System.out.println(intNum + "홀수입니다");
		}
			

	}

}
